import {
  DirectionTool
} from "../chunk-DVRMBPQ5.mjs";
import {
  ColorModeTool
} from "../chunk-4WQ67BIF.mjs";
import {
  ADDON_ID,
  COLOR_MODE_TOOL_ID,
  DIRECTION_TOOL_ID
} from "../chunk-QUP5TB6E.mjs";
import "../chunk-AKU6F3WT.mjs";

// src/preset/register.ts
import { addons, types } from "@storybook/addons";
addons.register(ADDON_ID, () => {
  const match = ({ viewMode }) => Boolean(viewMode && viewMode.match(/^(story|docs)$/));
  addons.add(DIRECTION_TOOL_ID, {
    type: types.TOOL,
    title: "Direction",
    render: DirectionTool,
    match
  });
  addons.add(COLOR_MODE_TOOL_ID, {
    type: types.TOOL,
    title: "Color Mode",
    render: ColorModeTool,
    match
  });
});
